import React, { useState } from "react";

const styles = {
  button: {
    width: "10%",
    height: 50,
    fontWeight: "bold",
    borderRadius: 10,
    fontSize: 18,
    backgroundColor: "#34b7f1",
    borderWidth: 0,
    color: "#fff",
  },
  textarea: {
    width: "60%",
    height: 50,
    borderRadius: 10,
    borderWidth: 0,
    padding: 10,
    fontSize: 18,
  },
  textContainer: {
    display: "flex",
    justifyContent: "space-evenly",
    alignItems: "center",
  },
};

export default function InputText({ addMessage }) {
  const [message, setMessage] = useState("");

  function addAMessage() {
    addMessage({
      message,
    });
    setMessage("");
  }

  function textareaAddMessage() {
    document.getElementById('textarea').addEventListener("keydown", function(e){
      if(e.keyCode === 13) {
        addAMessage();
      }
    })
  }

  const testMessage =(e)=> {
    console.log(e.target.value)
  }

  return (
    <div style={styles.textContainer}>
      <textarea
        id = "textarea"
        style={styles.textarea}
        rows={6}
        placeholder="write something..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyDown={() => textareaAddMessage()}
      ></textarea>
      <button onClick={(e) => addAMessage(e)} style={styles.button}>
        ENTER
      </button>
    </div>
  );
}
